from typing import List

def bitwiseSieve(srt, n):

    prime = [0 if i % 2 == 0 else 1 for i in range(n+1)]
    prime[1] = 0
    prime[2] = 1

    i = 3
    while (i * i <= n):
        if (prime[(i)] == 1):
            j = i * i
            while (j <= n):
                prime[j] = 0
                j += 2*i
        i += 2
    i = srt
    result=[]
    while (i <= n):
        if (prime[i] == 1):
            result.append(i)
        i += 1
    return result
class Solution:
    def primeSubOperation(self, nums: List[int]) -> bool:

        primeList=bitwiseSieve(1,1000)
        primeLength=len(primeList)
        length=len(nums)-2
        prev=nums[-1]

        while length>=0:
            num=nums[length]
            if num<prev:
                prev=num
                length-=1
                continue
            i=0
            prime=primeList[i]
            while num-prime>=prev:
                i+=1
                if i<primeLength:
                    prime=primeList[i]
                else:
                    return False
            prev=num-prime
            if prev<=0:
                return False
            length-=1
        return True

